SELECT axminions_users.id, axminions_users.uuid, axminions_users.name, axminions_users.texture, axminions_users.signature, axminions_users.extra_slots, axminions_users.island_slots, COUNT(*) FROM axminions_users, axminions_minions WHERE axminions_users.uuid = ? AND axminions_minions.owner_id = axminions_users.id GROUP BY axminions_minions.owner_id LIMIT 1;